import NavBar from "components/common/navbar/NavBar.vue";
import { getHomeMultidata } from "network/home";
/*用大括号导出是因为，导出的时候没有用default*/

export default {
  name: "Home",
  components: {
    NavBar: NavBar
  },
  data: function data() {
    return {
      banners: [],
      recommends: []
    };
  },
  created: function created() {
    var _this = this;

    /*首页创建之后发送网络请求*/
    //1.请求多个数据
    getHomeMultidata().then( //调用home.js封装的函数，获取url
    function (res) {
      console.log(res);
      _this.banners = res.banner;
      _this.recommends = res.recommend;
    });
  }
};
