import DetailNavBar from "./childComps/DetailNavBar";
import DetailSwiper from "./childComps/DetailSwiper";
import DetailBaseInfo from "./childComps/DetailBaseInfo";
import DetailShopInfo from "./childComps/DetailShopInfo";
import DetailGoodsInfo from "./childComps/DetailGoodsInfo";
import DetailParamInfo from "./childComps/DetailParamInfo";
import DetailCommentInfo from "./childComps/DetailCommentInfo";
import Scroll from "components/common/scroll/Scroll";
import { getDetail, Goods, Shop, GoodsParam } from "../../network/detail"; //传入事件

export default {
  name: "Detail",
  components: {
    DetailNavBar: DetailNavBar,
    DetailSwiper: DetailSwiper,
    DetailBaseInfo: DetailBaseInfo,
    DetailShopInfo: DetailShopInfo,
    Scroll: Scroll,
    DetailGoodsInfo: DetailGoodsInfo,
    DetailParamInfo: DetailParamInfo,
    DetailCommentInfo: DetailCommentInfo
  },
  data: function data() {
    return {
      iid: null,
      topImages: [],
      goods: {},
      shop: {},
      detailInfo: {},
      paramInfo: {},
      commentInfo: {}
    };
  },
  created: function created() {
    var _this = this;

    //保存传入的iid
    this.iid = this.$route.params.iid; //根据iid请求详情数据

    getDetail(this.iid).then(function (res) {
      //1获取顶部轮播图数据
      console.log(res);
      var data1 = res.data.result;
      _this.topImages = data1.itemInfo.topImages; //2获取商品信息

      _this.goods = new Goods(data1.itemInfo, data1.columns, data1.shopInfo.services); //3创建店铺信息

      _this.shop = new Shop(data1.shopInfo); //4保存商品详情数据

      _this.detailInfo = data1.detailInfo; //5获取参数信息

      _this.paramInfo = new GoodsParam(data1.itemParams.info, data1.itemParams.rule); //6获取评论信息

      if (data1.rate.list) {
        _this.commentInfo = data1.rate.list[0];
      }
    });
  }
};
