import HomeSwiper from "./childComps/HomeSwiper"; //导入子组件

import RecommendView from "./childComps/RecommendView";
import FeatureView from "./childComps/FeatureView";
import NavBar from "components/common/navbar/NavBar.vue"; //导入公共组件

import TabControl from "components/content/TabControl/TabControl";
import { getHomeMultidata } from "network/home";
/*用大括号导出是因为，导出的时候没有用default*/

export default {
  name: "Home",
  components: {
    NavBar: NavBar,
    HomeSwiper: HomeSwiper,
    RecommendView: RecommendView,
    FeatureView: FeatureView,
    TabControl: TabControl
  },
  data: function data() {
    return {
      banners: [],
      recommends: []
    };
  },
  created: function created() {
    var _this = this;

    /*首页创建之后发送网络请求*/
    //1.请求多个数据
    getHomeMultidata().then( //调用home.js封装的函数，获取url
    function (res) {
      //res会拿到请求的结果
      _this.banners = res.data.data.banner.list;
      _this.recommends = res.data.data.recommend.list;
    });
  }
};
