import {
  ADD_COUNTER,
  ADD_TO_CART
} from './mutations-type'

const mutations = {
  addCounter(state, payload) {
    payload.count++
  },
  addToCart(state, payload) {
    payload.checked = true
    state.cartList.push(payload)
  }
}


export default mutations
