import Vue from 'vue'
import Vuex from 'vuex'

//1.安装插件
Vue.use(Vuex)

//2.创建store对象
const store = new Vuex.Store({
   state:{
     cartList:[]//数组里面放很多对象
},
  mutations:{
     addCart(state,payload){
       state.cartList.push(payload)
     }
  }
})

//3.挂载到vue实例
export default store
