import Vue from 'vue'
import Vuex from 'vuex'

//1.安装插件
Vue.use(Vuex)

//2.创建store对象
const store = new Vuex.Store({
   state:{
     cartList:[]//数组里面放很多对象
},
  mutations:{
     addCart(state,payload){ //payload是传进来的product数据
       //payload新添加进去的商品
       let oldProduct=null
       for(let item of state.cartList){
         if(item.iid===payload.iid){
          oldProduct=item
         }
       }
       //2.判断oldProduct是否有值
       if (oldProduct){
         oldProduct.count+=1
       }else {
         oldProduct.count = 1
       }
       state.cartList.push(payload)
     }
  }
})

//3.挂载到vue实例
export default store
