import {
  ADD_COUNTER,
  ADD_TO_CART
} from './mutations-type'

const actions = {
  addCart(context, payload) {
      //1.查找之前数组中是否有该商品
      let oldProduct = context.state.cartList.find(item =>
        item.iid === payload.iid )
      //2.判断oldProduct是否有值
      if (oldProduct){
       // oldProduct.count+=1
        context.commit('addCounter',oldProduct)
      }else {
        payload.count = 1
        context.commit('addCart',payload)
      }
    }
  }

export default actions
