import DetailNavBar from "./childComps/DetailNavBar";
import DetailSwiper from "./childComps/DetailSwiper";
import DetailBaseInfo from "./childComps/DetailBaseInfo";
import DetailShopInfo from "./childComps/DetailShopInfo";
import DetailGoodsInfo from "./childComps/DetailGoodsInfo";
import DetailParamInfo from "./childComps/DetailParamInfo";
import DetailCommentInfo from "./childComps/DetailCommentInfo";
import GoodsList from "components/content/goods/GoodsList";
import DetailBottomBar from "./childComps/DetailBottomBar";
import BackTop from "components/content/backTop/BackTop";
import Scroll from "components/common/scroll/Scroll";
import { getDetail, Goods, Shop, GoodsParam, getRecommend } from "../../network/detail"; //传入事件

export default {
  name: "Detail",
  components: {
    DetailNavBar: DetailNavBar,
    DetailSwiper: DetailSwiper,
    DetailBaseInfo: DetailBaseInfo,
    DetailShopInfo: DetailShopInfo,
    Scroll: Scroll,
    DetailGoodsInfo: DetailGoodsInfo,
    DetailParamInfo: DetailParamInfo,
    DetailCommentInfo: DetailCommentInfo,
    GoodsList: GoodsList,
    DetailBottomBar: DetailBottomBar,
    BackTop: BackTop
  },
  data: function data() {
    return {
      iid: null,
      topImages: [],
      goods: {},
      shop: {},
      detailInfo: {},
      paramInfo: {},
      commentInfo: {},
      recommends: [],
      themeTopYs: [],
      //不应该写死，应该先获取各自的offsetTop值，参数，评论，推荐 再传入这个数组
      currentIndex: 0,
      isShowBackTop: false
    };
  },
  created: function created() {
    var _this = this;

    //保存传入的iid
    this.iid = this.$route.params.iid; //根据iid请求详情数据

    getDetail(this.iid).then(function (res) {
      //1获取顶部轮播图数据
      // console.log(res);
      var data1 = res.data.result;
      _this.topImages = data1.itemInfo.topImages; //2获取商品信息

      _this.goods = new Goods(data1.itemInfo, data1.columns, data1.shopInfo.services); //3创建店铺信息

      _this.shop = new Shop(data1.shopInfo); //4保存商品详情数据

      _this.detailInfo = data1.detailInfo; //5获取参数信息

      _this.paramInfo = new GoodsParam(data1.itemParams.info, data1.itemParams.rule); //6获取评论信息,但有些用户没有，这里需要做一下判断

      if (data1.rate.cRate !== 0) {
        _this.commentInfo = data1.rate.list[0];
      } //this.$nextTick( () => {//等到DetailParamsInfo全部渲染完成之后，才会回调一次下面的params函数
      //根据最新的数据，对应的DOM已经渲染出来 但是图片没有加载完，目前获取到offsetTop不包含图片，不能跳转到对应位置
      //this.themeTopYs=[];//每进来一次，都对这个新的空数组赋值
      //this.themeTopYs.push(0);
      //this.themeTopYs.push(this.$refs.params.$el.offsetTop);
      //this.themeTopYs.push(this.$refs.comment.$el.offsetTop);
      //this.themeTopYs.push(this.$refs.recommend.$el.offsetTop);
      // console.log(this.themeTopYs);
      // })

    }); //请求推荐数据

    getRecommend().then(function (res) {
      _this.recommends = res.data.data.list;
    });
  },
  mounted: function mounted() {//获取4个title的offsetTop不能再这里面获取，可能获取不到值，等渲染好之后，才会有值
  },
  updated: function updated() {//值不对，DOM没有渲染
    //this.themeTopYs=[];//每进来一次，都对这个新的空数组赋值
    // this.themeTopYs.push(0);
    //this.themeTopYs.push(this.$refs.params.$el.offsetTop);
    // this.themeTopYs.push(this.$refs.comment.$el.offsetTop);
    // this.themeTopYs.push(this.$refs.recommend.$el.offsetTop);
    //console.log(this.themeTopYs);
  },
  methods: {
    imageLoad: function imageLoad() {
      //let newRefresh =debouce(this.$refs.scroll.refresh, 100) 节流方法，图片都加载完，只刷新一次
      //newRefresh()
      this.themeTopYs = []; //每进来一次，都对这个新的空数组赋值

      this.themeTopYs.push(0);
      this.themeTopYs.push(this.$refs.params.$el.offsetTop);
      this.themeTopYs.push(this.$refs.comment.$el.offsetTop);
      this.themeTopYs.push(this.$refs.recommend.$el.offsetTop);
      this.themeTopYs.push(Number.MAX_VALUE);
    },
    contentScroll: function contentScroll(position) {
      //1.获取y值
      var positionY = -position.y;
      this.isShowBackTop = positionY > 1000; //2.positionY和主题中值进行对比
      //[ 0, 12664, 13952, 14169 , Number.MAX_VALUE]
      //positionY  在0和12664之间，index=0
      //12664和13952之间，index=1
      //13953和14169之间，index=2
      //大于 14169 index=3

      var length = this.themeTopYs.length;

      for (var i = 0; i < this.themeTopYs.length - 1; i++) {
        //不需要遍历最后一层
        // if(positionY>this.themeTopYs[i] && positionY<this.themeTopYs[i+1] ){
        // console.log(i);
        //}}
        // 方法1 很复杂
        //if(this.currentIndex!==i && (i<length-1&&positionY>=this.themeTopYs[i] && positionY<this.themeTopYs[i+1] )||// (i==length-1&&positionY>=this.themeTopYs[i])){
        // this.currentIndex=i
        // console.log(this.currentIndex);
        // this.$refs.nav.currentIndex=this.currentIndex;//使导航栏中的currengIndex等于这里的currentIndex
        // }
        //方法2 hack做法 给themeTops最后添加一个很大的值, 用于和最后一个主题的top进行比较
        if (this.currentIndex !== i && positionY >= this.themeTopYs[i] && positionY <= this.themeTopYs[i + 1]) {
          this.currentIndex = i;
          this.$refs.nav.currentIndex = this.currentIndex;
        }
      }
    },
    backTop: function backTop() {
      this.$refs.scroll.scroll.scrollTo(0, 0, 300);
    },
    addCart: function addCart() {
      //将商品加入到购物车
      //1.获取购物车需要展示的信息，添加进去
      var product = {};
      product.image = this.topImages[0];
      product.title = this.goods.title;
      product.desc = this.goods.desc;
      product.price = this.goods.newPrice;
      product.iid = this.iid; //2.将商品添加到购物车

      this.$store.commit('addCart', product);
    },
    titleClick: function titleClick(index) {
      //console.log(index);
      this.$refs.scroll.scrollTo(0, -this.themeTopYs[index], 100);
    }
  }
};
